        bytes32 /*agreementId*/,
        bytes calldata /*agreementData*/,
        bytes calldata /*cbdata*/,
        bytes calldata ctx
    )
        external override
        returns(bytes memory newCtx)
    {
        newCtx = ctx;
        ISuperfluid.Context memory context = _host.decodeCtx(ctx);
        (newCtx, ) = _host.callAgreementWithContext(
            _cfa,
            abi.encodeCall(
                _cfa.deleteFlow,
                (
                    superToken,
                    context.msgSender,
                    address(this),
